pragma solidity ^0.5.0;

contract ToDo {

    //@title This contract is to create structure of task and includes functions for CRUD
    //@author Medha Pandey

    struct Task{
        uint id;
        uint date;
        string content;
        string author;
        bool done;
    }

    Task[] public tasks;

    //@dev Create new Task
    function createTask(string memory _content, string memory _author) public{
        tasks.push(Task(tasks.length,block.timestamp,_content,_author,false));
    }

    //@dev Get a task
    function getTask(uint _id) public view 
    returns(
        uint,
        uint,
        string memory,
        string memory,
        bool 
    ){
        return(_id,tasks[_id].date,tasks[_id].content,tasks[_id].author,tasks[_id].done);
    }


}
